import React from 'react';
import { Col, Row, Nav, Card, Image, Button, Table, Dropdown, ProgressBar, Pagination, ButtonGroup } from '@themesberg/react-bootstrap';


const DisplayImagesFromContainer = ({blobList}) => (
    <div>
      <h2>Container items</h2>
      <Card border="light" className="shadow-sm">
      <Card.Body className="p-0">
      <Table responsive className="table-centered rounded" style={{ whiteSpace: 'pre-wrap', wordWrap: 'break-word' }}>
          <thead className="thead-light">
            <tr>
              <th className="border-0" style={{ width: '5%' }}>Image</th>
             
            </tr>
          </thead>
          <tbody>
        {blobList.map((item) => {
          return (

            <tr>
              <th>{item.name}</th>
              <th><a href='' download>Download</a>{item.name}</th>

            <th style={{ width: '5%' }}><Image src={"http://localhost:4000/images/" + item.image } style={{width:100}}/></th>
         
          </tr>
            // <li key={item.name}>
            //   <div>
            //     {item.name}
            //     <br />
            //     <img src={item.url} alt={item.name} height="200" />
            //   </div>
            // </li>
          );
        })}
         </tbody>
        </Table>
      </Card.Body>
    </Card>
    </div>
  );

  export default DisplayImagesFromContainer;
