import React from 'react';
import moment from "moment-timezone";
import Datetime from "react-datetime";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faCalendarAlt } from '@fortawesome/free-solid-svg-icons';
import { Col, Row, Nav, Card, Image, Button, Table, Dropdown, ProgressBar, Pagination, ButtonGroup } from '@themesberg/react-bootstrap';
import uploadFileToBlob, { isStorageConfigured, getBlobsInContainer } from './azure-storage-blob';
import {CanvasJSChart} from 'canvasjs-react-charts'



export default class NEWAPI extends React.Component {





  state = {
    data1 : [],
    refreshing:false,
    azureData:'',
    awsDate:''
}


componentDidMount(){
  this.getAzureDocument()
fetch('http://localhost:4000/dashboard', {
    method: 'GET',
    // mode: 'no-cors',
headers: {
'Access-Control-Allow-Origin':'*'
}
  })
  .then((response) => response.json())
    .catch((error) => console.error('Error', error))
    .then((result) => {
  console.log('result',result)
      if (result) {
        this.setState({
          refreshing: false,
          data1: result, //setstate of data which is convert in to fetch data and pass them in to component see below
        });

     
        // alert(result[0].timeleft)


      } else {
        this.setState({
          refreshing: false,
          data1: ['result'], //setstate of data which is convert in to fetch data and pass them in to component see below
        });
      }
    });

}


getAzureDocument(){
  getBlobsInContainer().then((list) =>{
   this.setState({azureData:list.length})
  })
}



getAwsChart(){
  fetch({
    url : "/channel-partner-dashboard/today-leaderboard",
    method : "GET",
  })
    .then((response) => response.json())
    .catch((error) => console.error('Error', error))
    .then((result) => {

      if(result){
        console.log('aaa',result)
        var month = [];
        var totalprice = []
       for(var i in result){
           month.push(result[i].agentname)
           totalprice.push(result[i].counter)
       }


   
      }
      else{
alert('Error Occureed')
      }
     
    
  
})


}


render(){


  


  const options = {
    animationEnabled: true,
    exportEnabled: true,
    theme: "light2", // "light1", "dark1", "dark2"
    title:{
      text: "Bounce Rate by Week of Year"
    },
    axisY: {
      title: "Bounce Rate",
      suffix: "%"
    },
    axisX: {
      title: "Week of Year",
      prefix: "W",
      interval: 2
    },
    data: [{
      type: "line",
      toolTipContent: "Week {x}: {y}%",
      dataPoints: [
        { x: 1, y: 64 },
        { x: 2, y: 61 },
        { x: 3, y: 64 },
        { x: 4, y: 62 },
        { x: 5, y: 64 },
        { x: 6, y: 60 },
        { x: 7, y: 58 },
        { x: 8, y: 59 },
        { x: 9, y: 53 },
        { x: 10, y: 54 },
        { x: 11, y: 61 },
        { x: 12, y: 60 },
        { x: 13, y: 55 },
        { x: 14, y: 60 },
        { x: 15, y: 56 },
        { x: 16, y: 60 },
        { x: 17, y: 59.5 },
        { x: 18, y: 63 },
        { x: 19, y: 58 },
        { x: 20, y: 54 },
        { x: 21, y: 59 },
        { x: 22, y: 64 },
        { x: 23, y: 59 }
      ]
    }]

  }

  let list = this.state.data1

  // console.log(list)

  if (list[0] == undefined) {

    return(
      <>
      <article>
      <p>Upload GDrive Documents</p>
      
      <form action='http://localhost:4000/insert/gdrive' method='post' encType='multipart/form-data'>
<input type='file' name='image' className='form-control'></input>
   

<button type='submit' className='btn btn-primary' style={{marginTop:40}}>Submit</button>
</form>
        

    </article>
      <p>Loading...</p>
      </>
    )

  } 
  
  else if (list[0][0].counter == undefined) {
    
    console.log('list',list[0][0].counter)
    
    return(
      <>
   
      <p>No Data Found</p>
      </>
    )

  } else {


  return (
    <>
  


<br/><br/>

<script src="https://canvasjs.com/assets/script/canvasjs.min.js"></script>
        <div className='container'>
          <div className='row'>
            
        
          <div className='col-lg-4'>
         <Card border="light" className="shadow-sm">
      <Card.Body className="p-5">

<h3>AWS Documents</h3>
  <p>Total Documents {list[0][0].counter}</p>

</Card.Body>
    </Card>

    </div>



    <div className='col-lg-4'>
         <Card border="light" className="shadow-sm">
      <Card.Body className="p-5">

<h3>Drive Documents</h3>
  <p>Total Documents {list[1][0].counter}</p>

</Card.Body>
    </Card>

    </div>



    <div className='col-lg-4'>
         <Card border="light" className="shadow-sm">
      <Card.Body className="p-5">

<h3>Azure Documents</h3>
  <p>Total Documents {this.state.azureData}</p>

</Card.Body>
    </Card>


    

    </div>




    <div className='col-lg-4'>
         <Card border="light" className="shadow-sm">
      <Card.Body className="p-5">

      <CanvasJSChart options = {options}
				/* onRef={ref => this.chart = ref} */
			/>

</Card.Body>
    </Card>


    

    </div>

 

         
</div>
        </div>
          {/* </tbody>
        </Table> */}
   

    </>
  );
  }
}
};
